import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import './styles/index.css';

// Emergency Simple Components
const EmergencyNavbar = () => (
  <nav className="bg-blue-600 text-white p-4">
    <div className="container mx-auto flex justify-between items-center">
      <Link to="/" className="text-xl font-bold">MatchCare</Link>
      <div className="space-x-4">
        <Link to="/" className="hover:text-blue-200">Home</Link>
        <Link to="/products" className="hover:text-blue-200">Products</Link>
        <Link to="/quiz" className="hover:text-blue-200">Skin Quiz</Link>
      </div>
    </div>
  </nav>
);

const EmergencyHomePage = () => (
  <div className="container mx-auto px-4 py-8">
    <div className="text-center">
      <h1 className="text-4xl font-bold text-gray-900 mb-4">
        Welcome to MatchCare
      </h1>
      <p className="text-xl text-gray-600 mb-8">
        AI-powered skincare recommendations for your skin type
      </p>
      <div className="space-x-4">
        <Link 
          to="/quiz" 
          className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 inline-block"
        >
          Take Skin Quiz
        </Link>
        <Link 
          to="/products" 
          className="bg-gray-200 text-gray-800 px-6 py-3 rounded-lg hover:bg-gray-300 inline-block"
        >
          Browse Products
        </Link>
      </div>
    </div>
  </div>
);

// Emergency Products Page (same as before)
const EmergencyProductsPage = () => {
  const [products, setProducts] = React.useState([]);
  const [loading, setLoading] = React.useState(true);
  const [error, setError] = React.useState(null);
  const [search, setSearch] = React.useState('');

  const fetchProducts = async (searchTerm = '') => {
    try {
      setLoading(true);
      const url = searchTerm 
        ? `http://localhost:5000/api/products?search=${encodeURIComponent(searchTerm)}&limit=20`
        : 'http://localhost:5000/api/products?limit=20';

      const response = await fetch(url);
      const data = await response.json();

      if (data.success) {
        setProducts(data.data || []);
        setError(null);
      } else {
        setError(data.message || 'Failed to fetch products');
      }
    } catch (err) {
      setError('Network error - is backend running on port 5000?');
    } finally {
      setLoading(false);
    }
  };

  React.useEffect(() => {
    fetchProducts();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    fetchProducts(search);
  };

  if (loading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading products...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
          <h2 className="text-lg font-semibold text-red-800 mb-2">Error</h2>
          <p className="text-red-600">{error}</p>
          <button 
            onClick={() => fetchProducts()}
            className="mt-4 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
          >
            Retry
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-6">Skincare Products</h1>

      <form onSubmit={handleSearch} className="mb-8">
        <div className="flex gap-2">
          <input
            type="text"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            placeholder="Search products..."
            className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <button
            type="submit"
            className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
          >
            Search
          </button>
        </div>
      </form>

      {products.length === 0 ? (
        <div className="text-center py-12">
          <p className="text-gray-600">No products found.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      )}
    </div>
  );
};

const ProductCard = ({ product }) => (
  <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
    <div className="p-6">
      <h3 className="text-lg font-semibold text-gray-900 mb-2">
        {product.name || 'Unknown Product'}
      </h3>
      <p className="text-sm text-gray-600 mb-2">
        Brand: {product.brand || 'Unknown Brand'}
      </p>
      {product.description && (
        <p className="text-gray-700 text-sm mb-4 line-clamp-3">
          {product.description.length > 100 
            ? product.description.substring(0, 100) + '...'
            : product.description
          }
        </p>
      )}
      <button className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 transition-colors">
        View Details
      </button>
    </div>
  </div>
);

// FIXED QUIZ PAGE - Working Version
const EmergencyQuizPage = () => {
  const [step, setStep] = React.useState(1);
  const [loading, setLoading] = React.useState(false);
  const [error, setError] = React.useState(null);
  const [quizData, setQuizData] = React.useState({
    skinType: '',
    concerns: [],
    sensitivities: []
  });
  const [sessionId, setSessionId] = React.useState(null);
  const [submitted, setSubmitted] = React.useState(false);

  // Start quiz session on component mount
  React.useEffect(() => {
    const startQuiz = async () => {
      try {
        setLoading(true);
        console.log('üéØ Starting quiz session...');

        const response = await fetch('http://localhost:5000/api/quiz/start', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' }
        });

        const data = await response.json();
        console.log('üìã Quiz start response:', data);

        if (data.success) {
          setSessionId(data.data.session_id);
          console.log('‚úÖ Quiz session started:', data.data.session_id);
        } else {
          setError('Failed to start quiz session');
        }
      } catch (err) {
        console.error('‚ùå Quiz start error:', err);
        setError('Network error - is backend running?');
      } finally {
        setLoading(false);
      }
    };

    startQuiz();
  }, []);

  const handleSkinTypeSelect = (skinType) => {
    console.log('üéØ Selected skin type:', skinType);
    setQuizData({ ...quizData, skinType });
    setStep(2);
  };

  const handleConcernToggle = (concern) => {
    const newConcerns = quizData.concerns.includes(concern)
      ? quizData.concerns.filter(c => c !== concern)
      : [...quizData.concerns, concern];
    console.log('üéØ Updated concerns:', newConcerns);
    setQuizData({ ...quizData, concerns: newConcerns });
  };

  const handleSensitivityToggle = (sensitivity) => {
    const newSensitivities = quizData.sensitivities.includes(sensitivity)
      ? quizData.sensitivities.filter(s => s !== sensitivity)
      : [...quizData.sensitivities, sensitivity];
    console.log('üéØ Updated sensitivities:', newSensitivities);
    setQuizData({ ...quizData, sensitivities: newSensitivities });
  };

  const submitQuiz = async () => {
    try {
      setLoading(true);
      console.log('üì§ Submitting quiz:', { sessionId, quizData });

      const response = await fetch('http://localhost:5000/api/quiz/submit', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          session_id: sessionId,
          skin_type: quizData.skinType,
          concerns: quizData.concerns,
          sensitivities: quizData.sensitivities
        })
      });

      const data = await response.json();
      console.log('üìã Quiz submit response:', data);

      if (data.success) {
        setSubmitted(true);
        console.log('‚úÖ Quiz submitted successfully');
      } else {
        setError('Failed to submit quiz');
      }
    } catch (err) {
      console.error('‚ùå Quiz submit error:', err);
      setError('Failed to submit quiz');
    } finally {
      setLoading(false);
    }
  };

  // Loading state
  if (loading && !sessionId) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Starting Your Skin Quiz</h2>
          <p className="text-gray-600">Preparing your personalized assessment...</p>
        </div>
      </div>
    );
  }

  // Error state
  if (error) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          <div className="bg-red-50 border border-red-200 rounded-lg p-6 text-center">
            <h2 className="text-lg font-semibold text-red-800 mb-2">Quiz Error</h2>
            <p className="text-red-600 mb-4">{error}</p>
            <button 
              onClick={() => window.location.reload()}
              className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Success state
  if (submitted) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto text-center">
          <div className="bg-green-50 border border-green-200 rounded-lg p-8">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg className="w-8 h-8 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M5 13l4 4L19 7" />
              </svg>
            </div>
            <h1 className="text-3xl font-bold text-green-800 mb-4">Quiz Complete!</h1>
            <div className="text-left bg-white rounded-lg p-4 mb-6">
              <h3 className="font-semibold text-gray-900 mb-2">Your Skin Profile:</h3>
              <p className="text-gray-700 mb-2">
                <span className="font-medium">Skin Type:</span> {quizData.skinType}
              </p>
              {quizData.concerns.length > 0 && (
                <p className="text-gray-700 mb-2">
                  <span className="font-medium">Main Concerns:</span> {quizData.concerns.join(', ')}
                </p>
              )}
              {quizData.sensitivities.length > 0 && (
                <p className="text-gray-700">
                  <span className="font-medium">Sensitivities:</span> {quizData.sensitivities.join(', ')}
                </p>
              )}
            </div>
            <div className="space-x-4">
              <Link 
                to="/products"
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 inline-block"
              >
                View Recommended Products
              </Link>
              <button
                onClick={() => {
                  setSubmitted(false);
                  setStep(1);
                  setQuizData({ skinType: '', concerns: [], sensitivities: [] });
                }}
                className="bg-gray-300 text-gray-700 px-6 py-3 rounded-lg hover:bg-gray-400 inline-block"
              >
                Retake Quiz
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Quiz steps
  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-2xl mx-auto">
        {/* Progress Bar */}
        <div className="mb-8">
          <div className="flex items-center justify-between mb-2">
            <span className="text-sm font-medium text-blue-600">Step {step} of 3</span>
            <span className="text-sm text-gray-500">{Math.round((step / 3) * 100)}% Complete</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div 
              className="bg-blue-600 h-2 rounded-full transition-all duration-300"
              style={{ width: `${(step / 3) * 100}%` }}
            ></div>
          </div>
        </div>

        <h1 className="text-3xl font-bold text-gray-900 mb-6 text-center">Skin Quiz</h1>

        {/* Step 1: Skin Type */}
        {step === 1 && (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-center">What's your skin type?</h2>
            <p className="text-gray-600 text-center mb-6">Choose the option that best describes your skin</p>
            <div className="space-y-3">
              {['Normal', 'Dry', 'Oily', 'Combination'].map(type => (
                <button
                  key={type}
                  onClick={() => handleSkinTypeSelect(type)}
                  className="w-full p-4 text-left border-2 border-gray-300 rounded-lg hover:border-blue-500 hover:bg-blue-50 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <div className="font-medium text-gray-900">{type}</div>
                </button>
              ))}
            </div>
          </div>
        )}

        {/* Step 2: Concerns */}
        {step === 2 && (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-center">What are your main skin concerns?</h2>
            <p className="text-gray-600 text-center mb-6">Select all that apply (you can choose multiple)</p>
            <div className="grid grid-cols-2 gap-3 mb-6">
              {['Acne', 'Wrinkles', 'Dryness', 'Oiliness', 'Dark Spots', 'Sensitivity'].map(concern => (
                <button
                  key={concern}
                  onClick={() => handleConcernToggle(concern)}
                  className={`p-3 rounded-lg border-2 transition-all duration-200 ${
                    quizData.concerns.includes(concern)
                      ? 'border-blue-500 bg-blue-50 text-blue-700'
                      : 'border-gray-300 hover:border-blue-300 hover:bg-gray-50'
                  }`}
                >
                  <div className="font-medium">{concern}</div>
                </button>
              ))}
            </div>
            <div className="flex gap-4">
              <button
                onClick={() => setStep(1)}
                className="flex-1 bg-gray-300 text-gray-700 py-3 rounded-lg hover:bg-gray-400 transition-colors"
              >
                Back
              </button>
              <button
                onClick={() => setStep(3)}
                className="flex-1 bg-blue-600 text-white py-3 rounded-lg hover:bg-blue-700 transition-colors"
              >
                Next
              </button>
            </div>
          </div>
        )}

        {/* Step 3: Sensitivities */}
        {step === 3 && (
          <div>
            <h2 className="text-xl font-semibold mb-4 text-center">Any known sensitivities?</h2>
            <p className="text-gray-600 text-center mb-6">Help us avoid ingredients that might irritate your skin</p>
            <div className="space-y-3 mb-6">
              {['Fragrance', 'Alcohol', 'Paraben', 'Sulfate'].map(sensitivity => (
                <button
                  key={sensitivity}
                  onClick={() => handleSensitivityToggle(sensitivity)}
                  className={`w-full p-3 text-left rounded-lg border-2 transition-all duration-200 ${
                    quizData.sensitivities.includes(sensitivity)
                      ? 'border-red-500 bg-red-50 text-red-700'
                      : 'border-gray-300 hover:border-red-300 hover:bg-gray-50'
                  }`}
                >
                  <div className="font-medium">{sensitivity}</div>
                </button>
              ))}
            </div>
            <div className="flex gap-4">
              <button
                onClick={() => setStep(2)}
                className="flex-1 bg-gray-300 text-gray-700 py-3 rounded-lg hover:bg-gray-400 transition-colors"
              >
                Back
              </button>
              <button
                onClick={submitQuiz}
                disabled={loading}
                className="flex-1 bg-green-600 text-white py-3 rounded-lg hover:bg-green-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >
                {loading ? 'Submitting...' : 'Complete Quiz'}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

